from browser import document, html

for i, ele in enumerate(document['Article'].select("h2")):
    ele.id = f"bookmark_{i}"
    if 'title' in ele.classList:
        document['Bookmark']<= html.A(href=f'#bookmark_{i}') <= html.DIV(ele.text, Class="menu-label is-size-5 has-text-dark")
    else:
        document['Bookmark']<= html.A(href=f'#bookmark_{i}') <= html.DIV(ele.text, Class="menu-list pl-6 my-2 has-text-dark")
