from browser import document, window

def ToggleNavbar(ev):
    ele = document['PufNavbar']
    ele2 = document['NavbarImpl']
    if 'is-active' in ele.classList:
        ele.classList.remove('is-active')
        ele2.classList.remove('is-active')
    else:
        ele.classList.add('is-active')
        ele2.classList.add('is-active')


document['NavbarImpl'].bind('click', ToggleNavbar)

def ToggleToTopVisibility(ev):
    ele = document['ToTopWidget']
    if window.pageYOffset >= 100:
        ele.classList.remove('is-fadeOut')
        ele.classList.add('is-fadeIn')
    else:
        ele.classList.remove('is-fadeIn')
        ele.classList.add('is-fadeOut')

document.bind('scroll', ToggleToTopVisibility)
